import React, {Component,Fragment} from 'react';
import {Card, Col, Container, Row} from "react-bootstrap";
import axios from "axios";
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import ApiURL from "../../api/ApiURL";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom';

class FooterDesktop extends Component {

    constructor() {
        super();
        this.state={

            footerData:"",
            androidLink:"",
            iosLink:"",
            facebookLink:"",
            twitterLink:"",
            instagramLink:"",
            address:"",
            aboutCompany:"",
            deliveryNotice:"",
            loaderDiv:"",
            mainDiv:"d-none",

        }
    }

    componentDidMount() {

        let SiteInfoPurchase_guide=sessionStorage.getItem('SiteInfoPurchase_guide')

        if(SiteInfoPurchase_guide==null){
            axios.get(ApiURL.SendSiteInfo)
                .then( res=>{
                    let StatusCord=res.status
                    if(StatusCord==200){
                        let JSONData=(res.data)[0]
                        this.setState({purchase_guide:JSONData,loaderDiv:'d-none',mainDiv:''})
                        sessionStorage.setItem('SiteInfoPurchase_guide',JSONData)
                    }
                    else {
                        toast.error('Something want wrong! Try again')
                    }



                })
                .catch(error=>{
                    toast.error('Something want wrong! Try again')
                })

        }else {
            this.setState({purchase_guide:SiteInfoPurchase_guide,loaderDiv:'d-none',mainDiv:''})
        }



    }






    render() {
        return (
            <Fragment>
                <div className='card'>
                    <Container fluid={true} className='pl-5'>
                        <Row className='px-0 my-5'>
                            <Col className='p-2 ' lg={3} md={3} sm={6} xs={12}>
                                <h5 className='footer-menu-title '>ABOUT COMPANY</h5>
                                <p>It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution</p>
                                <h5 className='footer-menu-title'>SOCIAL LINK</h5>
                                <a href=''><i className='fab m-1 h4 fa-facebook'></i></a>
                                <a href=''><i className='fab m-1 h4 fa-instagram'></i></a>
                                <a href=''><i className='fab m-1 h4 fa-twitter'></i></a>
                            </Col>
                            <Col className='p-2' lg={3} md={3} sm={6} xs={12}>
                                <h5 className='footer-menu-title '>THE COMPANY</h5>
                                <Link to='/about' href='' className='footer-link'>About us</Link>
                                <Link to= '/contact' href='' className='footer-link'>Contact us</Link>
                                <h5 className='footer-menu-title mt-3'>OFFICE ADDRESS</h5>
                                <p>192 SatarKulRoad, Uttor Badda,Dhaka-1212. 01818-103397 (Help-Line-01818-103397)</p>


                            </Col>
                            <Col className='p-2' lg={3} md={3} sm={6} xs={12}>
                                <h5 className='footer-menu-title'>MORE INFO</h5>
                                <Link to='/purchase' href='' className='footer-link'>How to Purchase</Link>
                                <Link to='/policy' href='' className='footer-link'>Privacy Policy</Link>
                                <Link to='/refund' href='' className='footer-link'>Refund Policy</Link>

                            </Col>
                            <Col className='p-2' lg={3} md={3} sm={6} xs={12}>
                                <h5 className='footer-menu-title'>DOWNLOAD APP</h5>
                                <a><img className="" src="https://hatil.com/sites/default/files/Apple%20sign.png"/></a><br/>
                                <a><img className="mt-2" src="https://hatil.com/sites/default/files/Android%20sign.png"/></a>
                                <p className='mt-1'>Change Language</p>
                                <p className='mt-3' id='google_translate_element'></p>



                            </Col>
                        </Row>

                        
                    

                    </Container>

                    
                    <Container fluid={true} className='pt-3 pb-1 bg-dark'>
                        <Container>
                            <Row className='px-0'>
                                <h6 className='text-white'>WE DELIVER IN</h6>
                                <p className='footer-text text-white'>It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution</p>


                            </Row>
                        </Container>

                       

                    </Container>

                </div>

            </Fragment>
        );
    }
}

export default FooterDesktop;
