import React, {Component,Fragment} from 'react';
import {Card, Col, Container, Row} from "react-bootstrap";
import axios from "axios";
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import ApiURL from "../../api/ApiURL";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Refund extends Component {


    constructor() {
        super();
        this.state={
            Refund:'',
            loaderDiv:"",
            mainDiv:"d-none",
        }
    }

    componentDidMount() {

        let SiteInfoPurchase_guide=sessionStorage.getItem('SiteInfoPurchase_guide')

        if(SiteInfoPurchase_guide==null){
            axios.get(ApiURL.SendSiteInfo)
                .then( res=>{
                    let StatusCord=res.status
                    if(StatusCord==200){
                        let JSONData=(res.data)[0]['purchase_guide']
                        this.setState({purchase_guide:JSONData,loaderDiv:'d-none',mainDiv:''})
                        sessionStorage.setItem('SiteInfoPurchase_guide',JSONData)
                    }
                    else {
                        toast.error('Something want wrong! Try again')
                    }



                })
                .catch(error=>{
                    toast.error('Something want wrong! Try again')
                })

        }else {
            this.setState({purchase_guide:SiteInfoPurchase_guide,loaderDiv:'d-none',mainDiv:''})
        }



    }


    render() {
        return (
            <Fragment>
                <Container className="TopSection">
                    <Row>


                        <Col className="mt-2" md={12} lg={12} sm={12} xs={12}>

                            <Card className={this.state.loaderDiv}>
                                <Card.Body>
                                    <div className="ph-item">
                                        <div className="ph-col-12">
                                            <div className="ph-row">
                                                <div className="ph-col-12"></div>
                                                <div className="ph-col-12"></div>
                                                <div className="ph-col-12"></div>
                                                <div className="ph-col-12"></div>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="ph-item">
                                        <div className="ph-col-12">
                                            <div className="ph-row">
                                                <div className="ph-col-12"></div>
                                                <div className="ph-col-12"></div>
                                                <div className="ph-col-12"></div>
                                                <div className="ph-col-12"></div>
                                            </div>
                                        </div>
                                    </div>

                                </Card.Body>
                            </Card>

                            <Card className={this.state.mainDiv}>
                                <Card.Body>
                                    { ReactHtmlParser(this.state.Refund) }
                                </Card.Body>
                            </Card>




                        </Col>


                    </Row>
                </Container>

                <ToastContainer />
            </Fragment>
        );
    }
}

export default Refund;
