import React, {Component,Fragment} from 'react';
import {Col, Container, Row, Card, Button, Form} from "react-bootstrap";
import validate from "../../validation/Validate";
import axios from "axios";
import ApiURL from "../../api/ApiURL";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Contact extends Component {

    constructor() {
        super();
        this.state={
            name:'',
            mobile:'',
            message:''
        }
    }

    nameOnChange=(event)=>{
        let name=event.target.value
        this.setState({name:name})
    }
    mobileOnChange=(event)=>{
        let mobile=event.target.value
        this.setState({mobile:mobile})
    }
    msgOnChange=(event)=>{
        let message=event.target.value
        this.setState({message:message})
    }


    onFromSubmit=(event)=>{
       let name=this.state.name
        let mobile=this.state.mobile
        let message=this.state.message
        let sendBtn=document.getElementById('sendBtn')
        let contactForm=document.getElementById('contactForm')



        if(name.length==0){
            toast.error('Enter your name')
        }
        if(mobile.length==0){
            toast.error('Enter your Mobile number')
        }

        else if(!(validate.NameRegx).test(name)){
            toast.error('invalid name')
        }
        else if(!(validate.MobileRegx).test(mobile)){
            toast.error('invalid mobile number')
        }
        else if(message.length==0){
            toast.error('Enter write message')
        }
        else {
            sendBtn.innerHTML= 'sending...'
            let MyFormData=new FormData()
            MyFormData.append('name',name)
            MyFormData.append('mobile',mobile)
            MyFormData.append('message',message)

            axios.post(ApiURL.SendContactDetails,MyFormData)
                .then(function (response){
                    if(response.status==200 && response.data==1){
                        toast.success('Request sucessfull')
                        sendBtn.innerHTML='send'
                        contactForm.reset()

                    }else {
                        toast.error('error')
                        sendBtn.innerHTML='send'
                    }

                })
                .catch(function (error){
                    sendBtn.innerHTML='send'

                })

        }
        event.preventDefault()
    }



    render() {
        return (
          <Fragment>
              <Container className='TopSection' >
                  <Row className='p-2'>
                      <Col lg={6} md={6} sm={6} xs={6} className='shadow-sm mt-2 bg-white'>
                          <Form id='contactForm' onSubmit={this.onFromSubmit} className='onBoardForm'>
                              <h4 className='section-title text-center'>CONTACT WITH  US</h4>
                              <h6 className="section-sub-title text-center">Please Enter Your Mobile No, And Go Next</h6>

                                  <input onChange={this.nameOnChange} className='form-control m-2' type='text' placeholder='Your Name' />


                                  <input onChange={this.mobileOnChange} className='form-control m-2' type='text' placeholder='Your Mobile' />


                                  <input onChange={this.msgOnChange} className='form-control m-2' type='text' placeholder='Message' />


                              <Button id='sendBtn' type='submit' className="btn btn-block m-2 mt-3 site-btn">Send</Button>

                          </Form>

                      </Col>
                      <Col className='p-0 m-0 'lg={6} md={6} sm={6} xs={6}>

                      <iframe className='googleMap'

                      src ="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3650.8339318426815!2d90.43269651429767!3d23.78892729322189!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3755c7c04bd74f81%3A0x96a565223dddb8a!2sSatarkul%20Rd%2C%20Dhaka!5e0!3m2!1sen!2sbd!4v1614970425476!5m2!1sen!2sbd">

                      </iframe>
                      </Col>

                  </Row>

              </Container>

              <ToastContainer />

          </Fragment>
        );
    }
}

export default Contact;
