import React, {Component,Fragment} from 'react';
import {Card, Col, Container, Row} from "react-bootstrap";
import axios from "axios";
import ReactHtmlParser, { processNodes, convertNodeToElement, htmlparser2 } from 'react-html-parser';
import ApiURL from "../../api/ApiURL";

class About extends Component {

    constructor() {
        super();
        this.state={
            about:''
        }
    }

    componentDidMount() {

        let SiteInfoAbout=sessionStorage.GetItem('SiteInfoAbout')

        if(SiteInfoAbout==null){
            axios.get(ApiURL.SendSiteInfo)
                .then( res=>{
                    let StatusCord=res.status
                    if(StatusCord==200){
                        let JSONData=(res.data)[0]['about']
                        this.setState({about:JSONData})
                        sessionStorage.setItem('SiteInfoAbout',JSONData)
                    }



                })
                .catch(error=>{

                })

        }else {
            this.setState({about:SiteInfoAbout})
        }



    }


    render() {
        return (
            <Fragment>
                <Container className='TopSection' >
                    <Row>
                        <Col className='mt-2' lg={12} md={12} sm={12} xs={12} >
                            <Card>
                                <Card.Body>
                                    { ReactHtmlParser(this.state.about) }
                                </Card.Body>
                            </Card>

                        </Col>

                    </Row>

                </Container>

            </Fragment>

        );
    }
}

export default About;
